mapper.select:
SELECT result1.* FROM (SELECT num,writer,title,content,viewCount, TO_CHAR(regdate,'YY.MM.DD HH24:MI') AS regdate, LAG(num,1,0) OVER (ORDER BY num DESC) AS prevNum, LEAD(num,1,0) OVER (ORDER BY num DESC) AS nextNum FROM sbnotice
WHERE title LIKE '%'||#{title}||'%' OR content LIKE '%'||#{content}||'%'
WHERE title LIKE '%'||#{title}||'%'
) result1 WHERE num=#{num}

mapper.update:
UPDATE sbnotice
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE sbnotice
SET title=#{title}, content=#{content}
WHERE num=#{num}
